import React, { useState } from "react";
import { useTranslation } from "react-i18next";
import circle from "../IMAGES/cirle.png";
import happy from "../IMAGES/happy.png";

export default function Donations() {
  const { t, i18n } = useTranslation();
  const [showTooltip, setShowTooltip] = useState(false);

  const handleMouseEnter = () => {
    setShowTooltip(true);
  };

  const handleMouseLeave = () => {
    setShowTooltip(false);
  };

  return (
    <html lang={i18n.language}>
    <div className="donations -mt-4 bg-white flex flex-col items-center justify-center min-h-screen">
      {/* Section 1: Heading */}
      <div
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          marginBottom: "20px",

          marginRight: "40px",

        }}
      >
        <a style={{ fontSize: "20px", color: "#F24E1E", textAlign: "left",fontSize: "25px" }}>
      {t("NavBar.Donations")}
    </a>
      </div>

      {/* Section 2: Description */}
      <p className="text-justify mb-4 text-gray-400 mx-auto max-w-[1100px]">
        {t("Donations.Description1")}
      </p>
      <p className="text-justify mb-4 text-gray-400 mx-auto max-w-[1100px] mb-10">
        {t("Donations.Description2")}
      </p>

      {/* Section 3: Donation Stats */}
      <div className="flex">
        <div className="w-1/2">
          <div className="flex items-center mb-4">
            <div>
              <img src={circle} alt="Donation" className="w-10 h-10 mr-2" />
            </div>
            <div>
              <p className="text-black font-bold text-left">
                {t("Donations.Stat1")}
              </p>
              <p className="text-gray-500 text-left text-sm">
                {t("Donations.Stat1description")}
              </p>
            </div>
          </div>
          <hr className="border-gray-300 mb-4 w-80 mx-auto" />
          <div className="flex items-center mb-4">
            <div>
              <img src={circle} alt="Donation" className="w-10 h-10 mr-2" />
            </div>
            <div>
              <p className="text-black font-bold text-left">
                {t("Donations.Stat2")}
              </p>
              <p className="text-gray-500 text-left text-sm ">
                {t("Donations.Stat2description")}
              </p>
            </div>
          </div>
          <hr className="border-gray-300 mb-4 w-80 mx-auto" />
          <div className="flex items-center">
            <div>
              <img src={circle} alt="Donation" className="w-10 h-10 mr-2" />
            </div>
            <div>
              <p className="text-black font-bold text-left">
                {t("Donations.Stat3")}
              </p>
              <p className="text-gray-500 text-left text-sm">
                {t("Donations.Stat3description")}
              </p>
            </div>
          </div>
        </div>

        {/* Section 4: Donation Image and Tooltip */}
        <div className="w-1/2 flex flex-col items-center">
          <div
            className="relative"
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
          >
            <img
              src={happy}
              alt={t("Donations.altHappy")}
              className="mb-4 h-full max-h-[266px] rounded-"
            />
            {showTooltip && (
              <div className="tooltip">
                {t("Donations.altHappy")}
              </div>
            )}
          </div>

          {/* Section 5: Donation Button */}
          <div className="mt-0 flex items-center items-center flex-col gap-x-6">
            <a
              href="donate"
              role="button"
              aria-label={t("Donations.ButtonLabel")}
              aria-disabled={true}
              className="rounded-full bg-orange-500 px-6 py-3 text-2xl font-semibold text-white shadow-xl focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-[bg-orange-500]"
              style={{
                transition: "transform 0.3s",
                transform: "scale(1)",
              }}
              onMouseEnter={(e) => (e.target.style.transform = "scale(1.1)")}
              onMouseLeave={(e) => (e.target.style.transform = "scale(1)")}
            >
              <span
                className="text-gray-900 text-2xl"
                style={{ letterSpacing: "-0.05em" }}
              >
                {t("Standard.D")}
              </span>
              <span
                className="text-white text-2xl"
                style={{ letterSpacing: "-0.05em" }}
              >
                {t("Standard.ON")}
              </span>
              <span
                className="text-gray-900 text-2xl"
                style={{ letterSpacing: "-0.05em" }}
              >
                {t("Standard.ATE")}
              </span>
              <span className="text-white text-2xl" style={{ marginLeft: "0.5em" }}>
                {t("Standard.TODAY")}
              </span>
            </a>
            <span className="sr-only">{t("Donations.ButtonLabel")}</span>
          </div>
        </div>
      </div>

      {/* Section 6: Styling for Tooltip */}
      <style>
        {`
        .tooltip {
          position: absolute;
          top: 50%;
          left: 50%;
          transform: translate(-50%, -50%);
          background-color: #FF6600;
          border-radius: 10px;
          padding: 8px;
          color: #fff;
          font-size: 12px;
        }
        `}
      </style>
    </div>
    </html>
  );
}
