import React, { useState } from 'react';
import { useTranslation } from "react-i18next";

const Bachelors = () => {
  const { t, i18n } = useTranslation();

  const [programFilter, setProgramFilter] = useState([]);
  const [ageFilter, setAgeFilter] = useState([]);
  const [languageFilter, setLanguageFilter] = useState([]);

  const data = [
    { program: 'Electrical Engineer', age: 20, language: 'German', image: 'computer.jpg', name: 'Sven' },
    { program: 'Computer Engineer', age: 20, language: 'Bilingual', image: 'software.jpg', name: 'Carl' },
    { program: 'Electrical Engineer', age: 20, language: 'Bilingual', image: 'electrical.jpg', name: 'Joshua' },
    { program: 'Chemical Engineer', age: 20, language: 'Bilingual', image: 'languages.jpg', name: 'Diana' },
    { program: 'Computer Engineer', age: 21, language: 'Spanish', image: 'computer.jpg', name: 'Mario' },
    { program: 'Computer Science', age: 23, language: 'Portuguese', image: 'software.jpg', name: 'Ruperto' },
    { program: 'Electrical Engineer', age: 21, language: 'Bilingual', image: 'languages.jpg', name: 'Nic' },
    { program: 'Software Engineer', age: 19, language: 'Italian', image: 'computer.jpg', name: 'Antonella' },
    { program: 'Mechanical Engineer', age: 22, language: 'Catalan', image: 'software.jpg', name: 'Efrain' },
    { program: 'Electrical Engineer', age: 20, language: 'Bilingual', image: 'languages.jpg', name: 'Steve' },
    { program: 'Computer Engineer', age: 21, language: 'French', image: 'computer.jpg', name: 'Rick' },
    { program: 'Mechanical Engineer', age: 22, language: 'Arab', image: 'software.jpg', name: 'Claudette' }
  ];

  const handleProgramFilterChange = (e) => {
    const selectedProgram = e.target.value;
    if (programFilter.includes(selectedProgram)) {
      setProgramFilter(programFilter.filter((program) => program !== selectedProgram));
    } else {
      setProgramFilter([...programFilter, selectedProgram]);
    }
  };

  const handleAgeFilterChange = (e) => {
    const selectedAge = e.target.value;
    if (ageFilter.includes(selectedAge)) {
      setAgeFilter(ageFilter.filter((age) => age !== selectedAge));
    } else {
      setAgeFilter([...ageFilter, selectedAge]);
    }
  };

  const handleLanguageFilterChange = (e) => {
    const selectedLanguage = e.target.value;
    if (languageFilter.includes(selectedLanguage)) {
      setLanguageFilter(languageFilter.filter((language) => language !== selectedLanguage));
    } else {
      setLanguageFilter([...languageFilter, selectedLanguage]);
    }
  };

  const clearFilters = () => {
    setProgramFilter([]);
    setAgeFilter([]);
    setLanguageFilter([]);
  };

  const filteredData = data.filter((item) => {
    if (programFilter.length > 0 && !programFilter.includes(item.program)) {
      return false;
    }
    if (ageFilter.length > 0 && !ageFilter.includes(String(item.age))) {
      return false;
    }
    if (languageFilter.length > 0 && !languageFilter.includes(item.language)) {
